mapper.update:
		update
		especial.room_review set BOARD_READCOUNT = BOARD_READCOUNT + 1 where
		room_review_no = #{room_review_no}

mapper.select:
select member_id,
member_pw from especial.room_review where room_review_no =
#{room_review_no}

mapper.update:
update especial.room_review set 
room_review_title = #{room_review_title},
room_review_contents = #{room_review_content}, 
room_review_file = #{room_review_file} 
where room_review_no = #{room_review_no}
